mapper.select:
SELECT ifnull(sum(1),0) ALL_COUNT, ifnull(sum( CASE WHEN V_STATUS = '1' THEN 1 WHEN V_STATUS = '3' THEN 1 END ),0) SUCCESS_COUNT, ifnull(sum(CASE WHEN V_PAY_TYPE = '01' then 1 END),0) ALI_COUNT, ifnull(sum( CASE WHEN V_PAY_TYPE = '02' THEN 1 END ),0) WX_COUNT FROM t_order_tab
and V_BELONG_APP = #{V_APP_ID}
and V_BELONG_USER = #{V_USER_ID}
