mapper.select:
select tot.*,tat.V_APP_NAME,tsub.USER_NAME,tpa.V_PAY_NAME,tft.V_NAME from t_order_tab tot left join t_app_tab tat on tot.V_BELONG_APP = tat.ID left join t_sys_user_base tsub on tot.V_BELONG_USER = tsub.USER_ID left join t_pay_account tpa on tot.V_BELONG_ACCOUNT = tpa.ID left join t_file_tab tft on tpa.V_FILE_ID = tft.ID
AND (tat.V_USER_ID = #{USER_ID} or tsub.USER_ID = #{USER_ID})
AND tot.V_STATUS = #{V_STATUS}
AND tsub.USER_NAME like CONCAT('%', #{USER_NAME}, '%')
AND tat.V_APP_NAME like CONCAT('%', #{V_APP_NAME}, '%')
AND tot.V_ORDER_NO like CONCAT('%', #{V_ORDER_NO}, '%')
AND tot.V_CREATE_TIME >= #{START_TIME}
AND tot.V_CREATE_TIME <= #{END_TIME}
order by tot.V_CREATE_TIME desc

select tot.*,tat.V_APP_NAME,tsub.USER_NAME,tpa.V_PAY_NAME from t_order_history tot left join t_app_tab tat on tot.V_BELONG_APP = tat.ID left join t_sys_user_base tsub on tot.V_BELONG_USER = tsub.USER_ID left join t_pay_account tpa on tot.V_BELONG_ACCOUNT = tpa.ID
AND (tat.V_USER_ID = #{USER_ID} or tsub.USER_ID = #{USER_ID})
AND tot.V_STATUS = #{V_STATUS}
AND tsub.USER_NAME like CONCAT('%', #{USER_NAME}, '%')
AND tat.V_APP_NAME like CONCAT('%', #{V_APP_NAME}, '%')
AND tot.V_ORDER_NO like CONCAT('%', #{V_ORDER_NO}, '%')
AND tot.V_CREATE_TIME >= #{START_TIME}
AND tot.V_CREATE_TIME <= #{END_TIME}
order by tot.V_CREATE_TIME desc

mapper.update:

update t_collection_tab set 
V_CASH_COLLECTION = V_CASH_COLLECTION + #{V_MONEY} ,
V_TOTAL_COLLECTION = V_TOTAL_COLLECTION + #{V_MONEY} ,
V_WX_COLLECTION =V_WX_COLLECTION+#{WX_MONEY},
V_ALI_COLLECTION = V_ALI_COLLECTION + #{ALI_MONEY}
where V_APP_ID = #{V_APP_ID}


update t_bond_today set 
V_LOCK_MONEY =V_LOCK_MONEY +  #{V_LOCK_MONEY} 
where V_USER_ID = #{V_USER_ID}


update t_account_show set 
V_PAY_NUM =V_PAY_NUM - 1
where V_ACCOUNT_ID = #{V_ACCOUNT_ID}
